import type { Express } from "express";
import { createServer, type Server } from "http";
import multer from "multer";
import { storage } from "./storage";
import { 
  analyzeCodebase, 
  generateCode, 
  chatWithAI, 
  analyzeDesignImage,
  type ChatMessage,
  type CodeGenerationRequest 
} from "./services/openai";
import { insertProjectSchema, insertDesignInputSchema } from "@shared/schema";
import { z } from "zod";

const upload = multer({ 
  storage: multer.memoryStorage(),
  limits: { fileSize: 10 * 1024 * 1024 } // 10MB
});

export async function registerRoutes(app: Express): Promise<Server> {

  // Get all projects
  app.get("/api/projects", async (req, res) => {
    try {
      const projects = await storage.getAllProjects();
      res.json(projects);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch projects" });
    }
  });

  // Create new project
  app.post("/api/projects", async (req, res) => {
    try {
      const projectData = insertProjectSchema.parse(req.body);
      const project = await storage.createProject(projectData);
      res.json(project);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid project data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to create project" });
      }
    }
  });

  // Get project by ID
  app.get("/api/projects/:id", async (req, res) => {
    try {
      const project = await storage.getProject(req.params.id);
      if (!project) {
        return res.status(404).json({ message: "Project not found" });
      }
      res.json(project);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch project" });
    }
  });

  // Update project
  app.patch("/api/projects/:id", async (req, res) => {
    try {
      const project = await storage.updateProject(req.params.id, req.body);
      if (!project) {
        return res.status(404).json({ message: "Project not found" });
      }
      res.json(project);
    } catch (error) {
      res.status(500).json({ message: "Failed to update project" });
    }
  });

  // Analyze codebase
  app.post("/api/analyze-codebase", async (req, res) => {
    try {
      const { codebase } = req.body;
      if (!codebase) {
        return res.status(400).json({ message: "Codebase is required" });
      }

      const analysis = await analyzeCodebase(codebase);
      res.json(analysis);
    } catch (error) {
      res.status(500).json({ message: (error as Error).message });
    }
  });

  // Generate code
  app.post("/api/generate-code", async (req, res) => {
    try {
      const request: CodeGenerationRequest = req.body;

      if (!request.description || !request.targetComponents || request.targetComponents.length === 0) {
        return res.status(400).json({ message: "Description and target components are required" });
      }

      const result = await generateCode(request);
      res.json(result);
    } catch (error) {
      res.status(500).json({ message: (error as Error).message });
    }
  });

  // Chat with AI
  app.post("/api/chat", async (req, res) => {
    try {
      const { messages, context, projectId } = req.body;

      if (!messages || !Array.isArray(messages)) {
        return res.status(400).json({ message: "Messages array is required" });
      }

      const response = await chatWithAI(messages, context);

      // Save conversation if projectId is provided
      if (projectId) {
        const updatedMessages = [...messages, { role: 'assistant', content: response, timestamp: new Date() }];
        await storage.updateConversation(projectId, updatedMessages);
      }

      res.json({ response });
    } catch (error) {
      res.status(500).json({ message: (error as Error).message });
    }
  });

  // Upload and analyze design images
  app.post("/api/upload-design", upload.array('screenshots', 5), async (req, res) => {
    try {
      const files = req.files as Express.Multer.File[];

      if (!files || files.length === 0) {
        return res.status(400).json({ message: "At least one image file is required" });
      }

      const analyses: string[] = [];
      const base64Images: string[] = [];

      for (const file of files) {
        const base64Image = file.buffer.toString('base64');
        base64Images.push(base64Image);

        const analysis = await analyzeDesignImage(base64Image);
        analyses.push(analysis);
      }

      res.json({ 
        analyses,
        base64Images,
        message: `Successfully analyzed ${files.length} design image(s)` 
      });
    } catch (error) {
      res.status(500).json({ message: (error as Error).message });
    }
  });

  // Create design input
  app.post("/api/design-inputs", async (req, res) => {
    try {
      const inputData = insertDesignInputSchema.parse(req.body);
      const designInput = await storage.createDesignInput(inputData);
      res.json(designInput);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid design input data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to create design input" });
      }
    }
  });

  // Get conversation for project
  app.get("/api/conversations/:projectId", async (req, res) => {
    try {
      const conversation = await storage.getConversation(req.params.projectId);
      res.json(conversation || { messages: [] });
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch conversation" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  type User, 
  type InsertUser, 
  type Project, 
  type InsertProject,
  type Conversation,
  type InsertConversation,
  type DesignInput,
  type InsertDesignInput
} from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  getAllProjects(): Promise<Project[]>;
  getProject(id: string): Promise<Project | undefined>;
  createProject(project: InsertProject): Promise<Project>;
  updateProject(id: string, updates: Partial<Project>): Promise<Project | undefined>;

  getConversation(projectId: string): Promise<Conversation | undefined>;
  createConversation(conversation: InsertConversation): Promise<Conversation>;
  updateConversation(projectId: string, messages: any[]): Promise<Conversation>;

  createDesignInput(input: InsertDesignInput): Promise<DesignInput>;
  getDesignInputsByProject(projectId: string): Promise<DesignInput[]>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private projects: Map<string, Project>;
  private conversations: Map<string, Conversation>;
  private designInputs: Map<string, DesignInput>;

  constructor() {
    this.users = new Map();
    this.projects = new Map();
    this.conversations = new Map();
    this.designInputs = new Map();

    // Initialize with sample data
    this.initializeSampleData();
  }

  private initializeSampleData() {
    // Create sample project
    const sampleProject: Project = {
      id: "sample-project-1",
      name: "E-commerce App",
      description: "A modern e-commerce application with React and Tailwind CSS",
      framework: "Next.js + Tailwind",
      progress: "78",
      userId: null,
      codebase: null,
      createdAt: new Date(),
      updatedAt: new Date(),
    };

    this.projects.set(sampleProject.id, sampleProject);

    // Create sample conversation
    const sampleConversation: Conversation = {
      id: randomUUID(),
      projectId: sampleProject.id,
      messages: [
        {
          role: 'assistant',
          content: "I've analyzed your existing codebase. I see you want to update the header navigation. I have a few questions to better understand your requirements:\n\n1. Search Functionality: Should the search include autocomplete suggestions?\n2. User Avatar: What options should appear in the dropdown menu?",
          timestamp: new Date(Date.now() - 300000),
        },
        {
          role: 'user',
          content: "Yes, include autocomplete for search. For the avatar dropdown, add Profile, Settings, and Logout options.",
          timestamp: new Date(Date.now() - 180000),
        },
        {
          role: 'assistant',
          content: "Perfect! I'll generate the updated header component with search autocomplete and user dropdown. Here's a preview of the changes I'll make to your Header.tsx component.",
          timestamp: new Date(Date.now() - 60000),
        }
      ],
      createdAt: new Date(),
      updatedAt: new Date(),
    };

    this.conversations.set(sampleProject.id, sampleConversation);
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }

  async getAllProjects(): Promise<Project[]> {
    return Array.from(this.projects.values());
  }

  async getProject(id: string): Promise<Project | undefined> {
    return this.projects.get(id);
  }

  async createProject(insertProject: InsertProject): Promise<Project> {
    const id = randomUUID();
    const project: Project = {
      ...insertProject,
      id,
      description: insertProject.description || null,
      framework: insertProject.framework || "Next.js + Tailwind",
      progress: insertProject.progress || "0",
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.projects.set(id, project);
    return project;
  }

  async updateProject(id: string, updates: Partial<Project>): Promise<Project | undefined> {
    const project = this.projects.get(id);
    if (!project) {
      return undefined;
    }

    const updatedProject = {
      ...project,
      ...updates,
      updatedAt: new Date(),
    };

    this.projects.set(id, updatedProject);
    return updatedProject;
  }

  async getConversation(projectId: string): Promise<Conversation | undefined> {
    return this.conversations.get(projectId);
  }

  async createConversation(insertConversation: InsertConversation): Promise<Conversation> {
    const id = randomUUID();
    const conversation: Conversation = {
      ...insertConversation,
      id,
      projectId: insertConversation.projectId || null,
      messages: insertConversation.messages || [],
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.conversations.set(insertConversation.projectId!, conversation);
    return conversation;
  }

  async updateConversation(projectId: string, messages: any[]): Promise<Conversation> {
    let conversation = this.conversations.get(projectId);

    if (!conversation) {
      conversation = await this.createConversation({
        projectId,
        messages,
      });
    } else {
      conversation = {
        ...conversation,
        messages,
        updatedAt: new Date(),
      };
      this.conversations.set(projectId, conversation);
    }

    return conversation;
  }

  async createDesignInput(insertInput: InsertDesignInput): Promise<DesignInput> {
    const id = randomUUID();
    const designInput: DesignInput = {
      ...insertInput,
      id,
      projectId: insertInput.projectId || null,
      figmaLink: insertInput.figmaLink || null,
      screenshots: insertInput.screenshots || [],
      targetComponents: insertInput.targetComponents || [],
      createdAt: new Date(),
    };
    this.designInputs.set(id, designInput);
    return designInput;
  }

  async getDesignInputsByProject(projectId: string): Promise<DesignInput[]> {
    return Array.from(this.designInputs.values()).filter(
      input => input.projectId === projectId
    );
  }
}

export const storage = new MemStorage();

