  ]);
  const { toast } = useToast();

  const uploadMutation = useMutation({
    mutationFn: async (formData: FormData) => {
      const response = await fetch("/api/upload-design", {
        method: "POST",
        body: formData,
        credentials: "include",
      });
      if (!response.ok) throw new Error("Upload failed");
      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Upload successful",
        description: data.message,
      });
    },
    onError: (error) => {
      toast({
        title: "Upload failed",
        description: (error as Error).message,
        variant: "destructive",
      });
    },
  });

  const generateMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest("POST", "/api/generate-code", data);
    },
    onSuccess: () => {
      toast({
        title: "Preview generated",
        description: "Check the AI chat for the generated code",
      });
    },
    onError: (error) => {
      toast({
        title: "Generation failed",
        description: (error as Error).message,
        variant: "destructive",
      });
    },
  });

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = event.target.files;
    if (!files || files.length === 0) return;

    const formData = new FormData();
    Array.from(files).forEach((file) => {
      formData.append("screenshots", file);
    });

    uploadMutation.mutate(formData);
  };

  const handleComponentToggle = (componentId: string) => {
    setComponents(prev =>
      prev.map(comp =>
        comp.id === componentId ? { ...comp, checked: !comp.checked } : comp
      )
    );
  };

  const handleSelectAll = () => {
    const allChecked = components.every(comp => comp.checked);
    setComponents(prev =>
      prev.map(comp => ({ ...comp, checked: !allChecked }))
    );
  };

  const handleGeneratePreview = () => {
    const selectedComponents = components.filter(comp => comp.checked).map(comp => comp.name);

    if (!description) {
      toast({
        title: "Description required",
        description: "Please provide a project description",
        variant: "destructive",
      });
      return;
    }

    if (selectedComponents.length === 0) {
      toast({
        title: "Components required",
        description: "Please select at least one component to modify",
        variant: "destructive",
      });
      return;
    }

    generateMutation.mutate({
      description,
      figmaLink: figmaLink || undefined,
      targetComponents: selectedComponents,
    });
  };

  return (
    <>
      <div className="p-6 border-b border-gray-200">
        <h2 className="text-lg font-semibold text-gray-900 mb-2">Describe Your Changes</h2>
        <p className="text-sm text-gray-600">Tell us what you want to modify, add, or improve in your application.</p>
      </div>

      <div className="flex-1 p-6 overflow-y-auto">
        {/* Description Input */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Project Description
          </label>
          <Textarea
            className="w-full h-32 resize-none"
            placeholder="I want to update the header navigation to include a search bar and user avatar dropdown. The search should be prominently displayed in the center..."
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            data-testid="textarea-description"
          />
        </div>

        {/* File Upload Section */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Additional Resources
          </label>
          <div className="space-y-3">
            {/* Figma Link */}
            <div>
              <label className="block text-xs font-medium text-gray-600 mb-1">
                Figma Design Link
              </label>
              <Input
                type="url"
                placeholder="https://figma.com/file/..."
                value={figmaLink}
                onChange={(e) => setFigmaLink(e.target.value)}
                data-testid="input-figma-link"
              />
            </div>

            {/* Screenshot Upload */}
            <div>
              <label className="block text-xs font-medium text-gray-600 mb-1">
                Screenshots/References
              </label>
              <label className="border-2 border-dashed border-gray-300 rounded-lg p-4 text-center hover:border-primary transition-colors cursor-pointer block">
                <Upload className="mx-auto mb-2 text-2xl text-gray-400" size={32} />
                <p className="text-sm text-gray-600">Drop screenshots here or click to upload</p>
                <p className="text-xs text-gray-500 mt-1">PNG, JPG, GIF up to 10MB</p>
                <input
                  type="file"
                  multiple
                  accept="image/*"
                  onChange={handleFileUpload}
                  className="hidden"
                  data-testid="input-file-upload"
                />
              </label>
            </div>
          </div>
        </div>

        {/* Component Selection */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Target Components
          </label>
          <div className="bg-gray-50 rounded-lg p-4">
            <div className="flex items-center justify-between mb-3">
              <span className="text-sm font-medium text-gray-700">
                Select components to modify:
              </span>
              <Button
                variant="ghost"
                size="sm"
                onClick={handleSelectAll}
                className="text-xs text-primary hover:text-blue-700"
                data-testid="button-select-all"
              >
                Select All
              </Button>
            </div>
            <div className="space-y-2">
              {components.map((component) => (
                <label
                  key={component.id}
                  className="flex items-center space-x-3 cursor-pointer"
                >
                  <Checkbox
                    checked={component.checked}
                    onCheckedChange={() => handleComponentToggle(component.id)}
                    data-testid={`checkbox-component-${component.id}`}
                  />
                  <div className="flex items-center space-x-2">
                    <i className="fas fa-file-code text-gray-400"></i>
                    <span className="text-sm text-gray-700">{component.name}</span>
                  </div>
                </label>
              ))}
            </div>
          </div>
        </div>

        {/* Action Buttons */}
        <div className="flex space-x-3">
          <Button
            className="flex-1 bg-primary text-white hover:bg-blue-700 transition-colors"
            onClick={handleGeneratePreview}
            disabled={generateMutation.isPending}
            data-testid="button-generate-preview"
          >
            <Wand2 className="mr-2" size={16} />
            <span>{generateMutation.isPending ? "Generating..." : "Generate Preview"}</span>
          </Button>
          <Button
            variant="outline"
            size="icon"
            data-testid="button-save"
          >
            <Save size={16} />
          </Button>
        </div>
      </div>
    </>
  );
}

client/src/components/FileExplorer.tsx.content:
import { useState } from "react";
import { ChevronRight, ChevronDown, File, Folder, FolderOpen, Search, Download } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { useQuery } from "@tanstack/react-query";
import type { Project } from "@shared/schema";

interface FileNode {
  name: string;
  type: 'file' | 'folder';
  children?: FileNode[];
  size?: string;
  modified?: string;
}

export default function FileExplorer() {
  const [expandedFolders, setExpandedFolders] = useState<Set<string>>(new Set(['src', 'components']));
  const [selectedFile, setSelectedFile] = useState<string | null>(null);
  const [searchQuery, setSearchQuery] = useState('');

  const { data: projects = [] } = useQuery<Project[]>({
    queryKey: ['/api/projects'],
  });

  // Get the most recently created project (your uploaded NextJS project)
  const currentProject = projects.length > 1 
    ? projects[projects.length - 1] // Most recent project
    : projects[0]; // Fallback to first if only one

  // Parse codebase into file structure - this will show your real uploaded files!
  const parseCodebase = (codebase: any): FileNode[] => {
    if (!codebase) return getDefaultFileStructure();

    try {
      // Handle different codebase formats
      if (typeof codebase === 'object' && codebase.files) {
        // If codebase has a files property, use it directly
        return buildFileTree(Object.keys(codebase.files));
      }

      if (typeof codebase === 'string') {
        const files = new Set<string>();
        const lines = codebase.split('\n').filter(line => line.trim());

        lines.forEach(line => {
          // Look for file paths - common Next.js/React patterns
          const patterns = [
            // File extensions
            /[\w\-\.\/]+\.(tsx?|jsx?|css|scss|json|md|html|svg|ico|png|jpg|jpeg|gif|js|ts|mjs|cjs)/gi,
            // Next.js specific patterns
            /pages\/[\w\-\.\/]+/gi,
            /components\/[\w\-\.\/]+/gi,
            /app\/[\w\-\.\/]+/gi,
            /src\/[\w\-\.\/]+/gi,
            /public\/[\w\-\.\/]+/gi,
            // Import patterns
            /from\s+['"]([^'"]+)['"]/gi,
            /import\s+['"]([^'"]+)['"]/gi,
          ];

          patterns.forEach(pattern => {
            const matches = line.match(pattern);
            if (matches) {
              matches.forEach(match => {
                let fileName = match;
                if (fileName.startsWith('from ') || fileName.startsWith('import ')) {
                  fileName = fileName.replace(/^(from|import)\s+['"]/, '').replace(/['"]$/, '');
                  if (!fileName.includes('.')) {
                    fileName += '.tsx'; // Assume React component
                  }
                }
                if (fileName.includes('/') || fileName.includes('.')) {
                  files.add(fileName.replace(/^\.\//, '')); // Remove ./ prefix
                }
              });
            }
          });
        });

        // Add common Next.js/React project files based on content
        if (codebase.includes('"next"') || codebase.includes('next.config')) {
          files.add('next.config.js');
          files.add('pages/_app.tsx');
          files.add('pages/index.tsx');
        }
        if (codebase.includes('package.json') || codebase.includes('"name"')) files.add('package.json');
        if (codebase.includes('tsconfig') || codebase.includes('typescript')) files.add('tsconfig.json');
        if (codebase.includes('tailwind')) files.add('tailwind.config.js');
        if (codebase.includes('README')) files.add('README.md');

        console.log('Codebase content preview:', codebase.substring(0, 500));
        console.log('Parsed files from codebase:', Array.from(files));
        return buildFileTree(Array.from(files));
      }

      return getDefaultFileStructure();
    } catch (error) {
      console.error('Error parsing codebase:', error);
      return getDefaultFileStructure();
    }
  };

  const buildFileTree = (filePaths: string[]): FileNode[] => {
    const root: FileNode[] = [];
    const nodeMap = new Map<string, FileNode>();

    filePaths.forEach(filePath => {
      const parts = filePath.split('/').filter(part => part);
      let currentPath = '';

      parts.forEach((part, index) => {
        const isLast = index === parts.length - 1;
        const fullPath = currentPath ? `${currentPath}/${part}` : part;

        if (!nodeMap.has(fullPath)) {
          const node: FileNode = {
            name: part,
            type: isLast ? 'file' : 'folder',
            children: isLast ? undefined : [],
            size: isLast ? `${Math.round(Math.random() * 50 + 1)}KB` : undefined,
            modified: 'Just uploaded'
          };

          nodeMap.set(fullPath, node);

          if (currentPath) {
            const parent = nodeMap.get(currentPath);
            if (parent && parent.children) {
              parent.children.push(node);
            }
          } else {
            root.push(node);
          }
        }

        currentPath = fullPath;
      });
    });

    return root.length > 0 ? root : getDefaultFileStructure();
  };

  const getDefaultFileStructure = (): FileNode[] => [
    {
      name: 'src',
      type: 'folder',
      children: [
        {
          name: 'components',
          type: 'folder',
          children: [
            { name: 'Header.tsx', type: 'file', size: '2.1KB', modified: '2 mins ago' },
            { name: 'Navigation.tsx', type: 'file', size: '1.8KB', modified: '5 mins ago' },
            { name: 'SearchBar.tsx', type: 'file', size: '1.2KB', modified: '10 mins ago' },
          ]
        },
        { name: 'App.tsx', type: 'file', size: '0.8KB', modified: '1 week ago' },
      ]
    },
    { name: 'package.json', type: 'file', size: '1.2KB', modified: '1 week ago' },
    { name: 'tsconfig.json', type: 'file', size: '0.5KB', modified: '1 week ago' },
  ];

  // Get file structure from uploaded project
  const fileStructure = currentProject?.codebase 
    ? parseCodebase(currentProject.codebase)
    : getDefaultFileStructure();

  const toggleFolder = (path: string) => {
    const newExpanded = new Set(expandedFolders);
    if (newExpanded.has(path)) {
      newExpanded.delete(path);
    } else {
      newExpanded.add(path);
    }
    setExpandedFolders(newExpanded);
  };

  const selectFile = (path: string) => {
    setSelectedFile(path);
  };

  const renderFileTree = (nodes: FileNode[], basePath = '') => {
    return nodes.map((node) => {
      const fullPath = basePath ? `${basePath}/${node.name}` : node.name;
      const isExpanded = expandedFolders.has(fullPath);
      const isSelected = selectedFile === fullPath;

      if (searchQuery && !node.name.toLowerCase().includes(searchQuery.toLowerCase())) {
        return null;
      }

      return (
        <div key={fullPath}>
          <div
            className={`flex items-center space-x-2 py-1 px-2 hover:bg-gray-100 cursor-pointer ${
              isSelected ? 'bg-blue-50 border-r-2 border-blue-500' : ''
            }`}
            onClick={() => {
              if (node.type === 'folder') {
                toggleFolder(fullPath);
              } else {
                selectFile(fullPath);
              }
            }}
            data-testid={`file-tree-item-${fullPath}`}
          >
            <div className="flex items-center space-x-1 flex-1">
              {node.type === 'folder' ? (
                <>
                  {isExpanded ? (
                    <ChevronDown size={16} className="text-gray-500" />
                  ) : (
                    <ChevronRight size={16} className="text-gray-500" />
                  )}
                  {isExpanded ? (
                    <FolderOpen size={16} className="text-blue-500" />
                  ) : (
                    <Folder size={16} className="text-blue-500" />
                  )}
                </>
              ) : (
                <>
                  <div className="w-4"></div>
                  <File size={16} className="text-gray-400" />
                </>
              )}
              <span className={`text-sm ${isSelected ? 'font-medium text-blue-700' : 'text-gray-700'}`}>
                {node.name}
              </span>
            </div>

            {node.type === 'file' && (
              <div className="flex items-center space-x-2 text-xs text-gray-500">
                <span>{node.size}</span>
                <span>â€¢</span>
                <span>{node.modified}</span>
              </div>
            )}
          </div>

          {node.type === 'folder' && isExpanded && node.children && (
            <div className="ml-4 border-l border-gray-200">
              {renderFileTree(node.children, fullPath)}
            </div>
          )}
        </div>
      );
    });
  };

  return (
    <div className="h-full flex flex-col">
      {/* Header */}
      <div className="p-4 border-b border-gray-200 bg-white">
        <div className="flex items-center justify-between mb-3">
          <div>
            <h2 className="text-lg font-semibold text-gray-900">File Explorer</h2>
            <p className="text-sm text-gray-600">
              {currentProject ? `${currentProject.name} Files` : 'No project selected'}
            </p>
          </div>
          <Button
            variant="outline"
            size="sm"
            data-testid="button-download-project"
          >
            <Download size={16} className="mr-2" />
            Download
          </Button>
        </div>

        {/* Search */}
        <div className="relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={16} />
          <Input
            placeholder="Search files..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="pl-9"
            data-testid="input-file-search"
          />
        </div>
      </div>

      {/* File Tree */}
      <div className="flex-1 overflow-auto p-2">
        {currentProject ? (
          <div className="space-y-1">
            {renderFileTree(fileStructure)}
          </div>
        ) : (
          <div className="flex flex-col items-center justify-center h-full text-gray-500">
            <Folder size={48} className="mb-3" />
            <p className="text-sm">No project loaded</p>
            <p className="text-xs text-gray-400">Create a new project to see files</p>
          </div>
        )}
      </div>

      {/* Selected File Info */}
      {selectedFile && currentProject && (
        <div className="border-t border-gray-200 p-3 bg-gray-50">
          <div className="text-sm">
            <div className="font-medium text-gray-900">{selectedFile}</div>
            <div className="text-gray-500 mt-1">
              From {currentProject.name} â€¢ Ready for AI updates
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

client/src/components/MainContent.tsx.content:
import { useState } from "react";
import DesignInput from "@/components/DesignInput";
import AIChat from "@/components/AIChat";
import CodePreview from "@/components/CodePreview";
import LivePreview from "@/components/LivePreview";
import FileExplorer from "@/components/FileExplorer";
import { Button } from "@/components/ui/button";

export default function MainContent() {
  const [activeTab, setActiveTab] = useState<'design' | 'code' | 'preview' | 'files'>('design');

  const tabs = [
    { id: 'design' as const, label: 'Design Input' },
    { id: 'code' as const, label: 'Code Preview' },
    { id: 'preview' as const, label: 'Live Preview' },
    { id: 'files' as const, label: 'File Explorer' },
  ];

  return (
    <main className="flex-1 overflow-hidden">
      <div className="h-full flex flex-col">
        {/* Tab Navigation */}
        <div className="bg-white border-b border-gray-200 px-6">
          <div className="flex space-x-8">
            {tabs.map((tab) => (
              <Button
                key={tab.id}
                variant="ghost"
                className={`py-4 px-1 border-b-2 font-medium text-sm transition-colors ${
                  activeTab === tab.id
                    ? 'border-primary text-primary'
                    : 'border-transparent text-gray-500 hover:text-gray-700'
                }`}
                onClick={() => setActiveTab(tab.id)}
                data-testid={`tab-${tab.id}`}
              >
                {tab.label}
              </Button>
            ))}
          </div>
        </div>

        <div className="flex-1 overflow-hidden">
          <div className="h-full flex">
            {/* Left Panel - Input Section */}
            <div className="w-1/2 border-r border-gray-200 bg-white flex flex-col">
              {activeTab === 'design' && <DesignInput />}
              {activeTab === 'code' && <CodePreview />}
              {activeTab === 'preview' && <LivePreview />}
              {activeTab === 'files' && <FileExplorer />}
            </div>

            {/* Right Panel - AI Interaction */}
            <AIChat />
          </div>
        </div>
      </div>
    </main>
  );
}

client/src/components/NewProjectDialog.t.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Upload, Plus } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

const newProjectSchema = z.object({
  name: z.string().min(1, "Project name is required"),
  description: z.string().optional(),
  framework: z.string().min(1, "Framework is required"),
});

type NewProjectForm = z.infer<typeof newProjectSchema>;

interface NewProjectDialogProps {
  children: React.ReactNode;
}

export default function NewProjectDialog({ children }: NewProjectDialogProps) {
  const [open, setOpen] = useState(false);
  const [codebaseFile, setCodebaseFile] = useState<File | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<NewProjectForm>({
    resolver: zodResolver(newProjectSchema),
    defaultValues: {
      name: "",
      description: "",
      framework: "Next.js + Tailwind",
    },
  });

  const createProjectMutation = useMutation({
    mutationFn: async (data: NewProjectForm & { codebase?: string }) => {
      return apiRequest("POST", "/api/projects", data);
    },
    onSuccess: () => {
      toast({
        title: "Project created",
        description: "Your new project has been created successfully",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/projects'] });
      setOpen(false);
      form.reset();
      setCodebaseFile(null);
    },
    onError: (error) => {
      toast({
        title: "Error creating project",
        description: (error as Error).message,
        variant: "destructive",
      });
    },
  });

  const handleCodebaseUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      setCodebaseFile(file);
    }
  };

  const onSubmit = async (data: NewProjectForm) => {
    let codebaseContent = "";

    if (codebaseFile) {
      try {
        codebaseContent = await codebaseFile.text();
      } catch (error) {
        toast({
          title: "File upload error",
          description: "Failed to read codebase file",
          variant: "destructive",
        });
        return;
      }
    }

    createProjectMutation.mutate({
      ...data,
      codebase: codebaseContent || undefined,
    });
  };

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        {children}
      </DialogTrigger>
      <DialogContent className="max-w-md">
        <DialogHeader>
          <DialogTitle>Create New Project</DialogTitle>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <FormField
              control={form.control}
              name="name"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Project Name</FormLabel>
                  <FormControl>
                    <Input 
                      placeholder="My React App" 
                      {...field} 
                      data-testid="input-project-name"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="description"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Description (Optional)</FormLabel>
                  <FormControl>
                    <Textarea 
                      placeholder="Brief description of your project..." 
                      {...field} 
                      data-testid="textarea-project-description"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="framework"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Framework</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-framework">
                        <SelectValue placeholder="Select framework" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="Next.js + Tailwind">Next.js + Tailwind</SelectItem>
                      <SelectItem value="React + Tailwind">React + Tailwind</SelectItem>
                      <SelectItem value="React Native">React Native</SelectItem>
                      <SelectItem value="Vite + React">Vite + React</SelectItem>
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="space-y-2">
              <label className="text-sm font-medium text-gray-700">
                Upload Existing Codebase (Optional)
              </label>
              <label className="border-2 border-dashed border-gray-300 rounded-lg p-4 text-center hover:border-primary transition-colors cursor-pointer block">
                <Upload className="mx-auto mb-2 text-gray-400" size={24} />
                <p className="text-sm text-gray-600">
                  {codebaseFile ? codebaseFile.name : "Drop codebase files here or click to upload"}
                </p>
                <p className="text-xs text-gray-500 mt-1">ZIP, TXT, or individual files</p>
                <input
                  type="file"
                  accept=".zip,.txt,.js,.jsx,.ts,.tsx,.json"
